package org.yamcs.tctm;

import org.yamcs.utils.TimeEncoding;

public class TmContainer {
    private byte[] containerPayload;
    private long gentime = TimeEncoding.INVALID_INSTANT; // generation time

    /**
     * The time when the packet has been generated by the payload.
     * 
     * @return
     */
    public long getGenerationTime() {
        return gentime;
    }

    public TmContainer(byte[] containerPayload, long gentime) {
        this.gentime = gentime;
        this.containerPayload = containerPayload;
    }

    public byte[] getContainerPayload() {
        return this.containerPayload;
    }
}
